INSERT INTO Product (id, name, product_sdesc, product_ldesc, price) VALUES
(1, 'Beats Solo Wireless', 'Beats Solo3 Wireless. Für Sound gemacht. Auf Emotion getuned.', 'Mit bis zu 40 Stunden Batterielaufzeit sind die Beats Solo3 Wireless perfekt für den Alltag und jeden Tag. Mit Fast Fuel 5 Minuten Aufladen für 3 Stunden Wiedergabe. Erlebe ausgezeichneten Beats Sound mit kabelloser Bluetooth Class 1 Freiheit. Du kannst die gepolsterten Ohrmuscheln so einstellen, dass sie den ganzen Tag perfekt und komfortabel sitzen.', '140.00'),
(2, 'Apple iPhone XS', 'Willkommen auf dem grossen Display.', 'Das iPhone Xs kommt mit einem 5,8" Super Retina Display mit eigens gefertigten OLED Panels für ein HDR Display mit der besten Farbgenauigkeit der Branche, tiefem Schwarz und beeindruckender Helligkeit. Mit dem weiterentwickelten Face ID kannst du auf einen Blick dein iPhone sicher entsperren, dich bei Apps anmelden und bezahlen.', '1389.00' ),
(3, 'Huawei Matebook X Pro', 'Eröffnet neue Horizonte', 'Das erstes Notebook von Huawei im FullView-Design. Das Screen-to-Body-Ratio von 91% sorgt trotz der kompakten Grösse für ein riesiges, nahezu rahmenloses Display. Eine revolutionäres Erlebnis. Ein Bildschirm, der neue Welten eröffnet.', '2099.00'),
(4, 'HP Envy 27-b270nz', '27", Intel Core i7-8700T, 16GB, SSD, HDD',  'Durch sein durchdachtes Design und seine herausragende Leistung verwandelt der HP ENVY All-in-One Ihren Arbeitsplatz in einen Gallerieraum. Er kombiniert ein flaches und modernes Design mit der neusten Technologie in einem Gerät, auf dem Sie nicht nur arbeiten möchten – Sie möchten es der Welt zeigen.','2099.00'),
(5, 'Samsung C49HG90', 'Gaming neu definiert. Erlebe den bahnbrechenden 49" grossen und 32:9 breiten Monitor mit der neuen Metal Quantum Dot-Technologie und VA-Panel mit einer Sekunde Reaktionszeit und 144Hz.', 'Gaming neu definiert, entdecken Sie den bahnbrechenden 49" grossen und 32:9 breiten Monitor mit der neuen Metal Quantum Dot-Technologie und einem VA-Panel mit 1 ms MPRT. Ultrabreiter Bildschirm, der ultrabreite 49" Monitor mit einem Seitenverhältnis von 32:9 sorgt dafür, dass Sie immer alle Spielszenen im Blick haben. Das 1800R Curved Panel lässt Sie regelrecht in das Spiel eintauchen und liefert beste Kinostimmung.', '799.00');
